// from the GPU.
func (ml *Model) StepGet(vars ...GPUVars) {
	params := GetParams(0)
	RunStepInit(1)
	ml.StepCollision()
	ml.StepJointForces()
	ml.StepIntegrateBodies()

	for range params.Iterations {
		ml.StepSolveJoints()
		ml.StepBodyContacts()
	}
	RunDone(vars...)
}

